import { Container } from "react-bootstrap";
import NavBar from "./Componants/NavBar";
import MoviesList from "./Componants/MoviesList";
import { useEffect, useState } from "react";
import axios from 'axios'
function App() {
  const [movies, setMovies] = useState([])

  // Get All Movies By Axios
  const getAllMovies = async () => {
    const res = await axios.get("https://api.themoviedb.org/3/movie/popular?api_key=9896037be7d85d7cbca87d0f2b0774c3&language=ar")
    setMovies(res.data.results)
  }

  useEffect(() => {
    getAllMovies()
  }, [])

  // To Search In API
  const search = async (word) => {
    if (word !== "") {
      const res = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=9896037be7d85d7cbca87d0f2b0774c3&query=${word}&language=ar`)
      setMovies(res.data.results)
    } else {
      const res = await axios.get("https://api.themoviedb.org/3/movie/popular?api_key=9896037be7d85d7cbca87d0f2b0774c3&language=ar")
      setMovies(res.data.results)
    }
  }

  return (
    <div className="fs-2">
      <NavBar search={search}/>
      <Container>
        <MoviesList movies={movies}/>
      </Container>
    </div>
  );
}

export default App;
